    private final static String PASSWORD_PATTERN = "^(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])(?=\\S+$).{8,}$";
    private final static String NAME_PATTERN = "[A-Z][a-zA-Z]*";
    private final static String COUNTRY_PATTERN = "^[A-Z][a-z]+( [A-Z][a-z]+)*$";

    @Override
    public boolean isValid(UserDTO userDTO) {
        Pattern fieldPattern = Pattern.compile(EMAL_PATTERN);
        if (!fieldPattern.matcher(userDTO.getEmail()).matches()) {
            return false;
        } else {
            fieldPattern = Pattern.compile(PASSWORD_PATTERN);
            if (!fieldPattern.matcher(userDTO.getPassword()).matches()) {
                return false;
            } else {
                fieldPattern = Pattern.compile(NAME_PATTERN);
                if (!(fieldPattern.matcher(userDTO.getName()).matches() && fieldPattern.matcher(userDTO.getSurname()).matches())) {
                    return false;
                } else {
                    fieldPattern = Pattern.compile(COUNTRY_PATTERN);
                    if (!fieldPattern.matcher(userDTO.getCountry()).matches()) {
                        return false;
                    } else {
                        return userDTO.getPassword().equals(userDTO.getMatchingPassword());
                    }
                }
            }
        }
    }

